
public class Addition_Three_Numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a=3;
		int b=10;
		int c=11;
	

	if(a<b || a<c) {
		System.out.println(a+b +","+ (a+c));
				
	}
	
	else if(b<a || b<c) {
		System.out.println(b+c +","+ (b+a));
		
	}
	
	else if(c<a || c<b) {
System.out.println(c+a +","+(c+b));		
		
		
	}
		
		
		
		
		
		
	
	}

}
