
public class Integers_Odd_Even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
			
			for(int i=1; i<=5;i++) {
				
				if(i%2==1) {
				System.out.println("ODD"+"    "+i);
							}	
				else if(i%2==0) {
				System.out.println("EVEN"+"    "+i);
		      }
				
				
			}
			
		}
			
		}
				


